View(AA)

#Unir las listas en un solo data frame#
LISTA<-Reduce(function(x, y) merge(x, y, all=TRUE), AA)

#Eliminar registros duplicados#
LISTA2 <-distinct(LISTA)

#Unir los países por planta#
LISTA3 <-aggregate(NAME_ES ~ target_taxon_name , data=LISTA2, FUN=paste, collapse='|')

#Eliminar registros repetidos#
LISTA4 <-distinct(LISTA3)
View(LISTA3)

#Escribir archivo
write.csv(LISTA4, "listado_paises.csv", fileEncoding = "UTF-8")
